MATCH (:AzureAccount{id: {AZURE_SUBSCRIPTION_ID}})-[:RESOURCE]->(:AzureDatabaseAccount{id: {DATABASE_ACCOUNT_ID}})-[:CONTAINS]->(corspolicy:AzureCosmosDBCorsPolicy) WHERE EXISTS(corspolicy.allowedorigins)
 WITH corspolicy LIMIT {LIMIT_SIZE}
REMOVE corspolicy.allowedorigins, corspolicy.allowedmethods, corspolicy.allowedheaders, corspolicy.exposedheaders, corspolicy.maxageinseconds return COUNT(*) as TotalCompleted
